import React,{Component} from 'react';

//Class bases component
//state
export default class App extends Component {
    constructor(props){
        super(props);
        this.state={
            rollNo: "49",
            name1 : this.props.name
        }
    }
    handleing(){
        useState("saurav papa")
    }
    render(){
        return(
            // <React.Fragment>
            <div>
                <h1>hii darling {this.state.name1} and {this.state.rollNo}</h1>
                <button type="button" onClick={this.handleing}>Clicke me</button>
            </div>
            // </React.Fragment>
        )
    }
}

